import React, { Component } from "react";

const DICTIONARY = "abcdefghijklmnopqrstuvwuxyzABCDEFGHIJKLMNOPQRSTUVWXYZ";

class AddUrl extends Component {
    state = {
        url: "",
        encodeNumber: 101023,
        munchList: [],
        key: "",
    };

    encode(num) {
        const base = DICTIONARY.length;
        let encoded = "";

        if (num === DICTIONARY[0]) {
            return DICTIONARY[0];
        }

        while (num > 0) {
            encoded += DICTIONARY[num % base];
            num = Math.floor(num / base);
        }

        return this.reverseWord(encoded);
    }

    reverseWord(str) {
        let reverse = "";
        for (let i = str.length - 1; i >= 0; i--) {
            reverse += str.charAt(i);
        }
        return reverse;
    }

    urlHandler = (e) => {
        this.setState({ url: e.target.value });
    };

    submitHandler = (e) => {
        e.preventDefault();

        let list = {};
        const key = this.encode(this.state.encodeNumber);
        list["key"] = key;
        list["prop"] = this.state.url;
        //console.log("key", key);
        this.setState({
            munchList: [...this.state.munchList, list],
            encodeNumber: this.state.encodeNumber + 531,
        });

        console.log(this.state.munchList);
    };

    render() {
        return (
            <div>
                <form onSubmit={this.submitHandler}>
                    <input
                        value={this.state.url}
                        className="addUrl"
                        onChange={this.urlHandler}
                        type="text"
                        placeholder="Enter Url..."
                    />
                </form>
                <h1>Url list</h1>
                <hr />
                <div className="container">
                    <div>
                        {/* {this.state.munchList((url) => (
                            <>
                                <h3>google </h3>
                                <button className="url-btn">
                                    <a
                                        target="_blank"
                                        href="#"
                                    >{`Munch.ly/${url.key}`}</a>
                                </button>
                            </>
                        ))} */}
                    </div>
                </div>
            </div>
        );
    }
}
export default AddUrl;
