import React, { Component } from 'react';
import './App.css';
import AddUrl from './components/AddUrl'
// import UrlList from './components/UrlList'
import Header from './components/Header'
import SubHeader from './components/SubHeader'


class App extends Component {
  state = {
    url: "",
    encodeNumber: 101021,
    munchList: Links,
    key: "",
    color: ["orange", "black", "lightpurple", "pink", "yellow", "darkred"],
    currentColor: "orange",
  };

  urlHandler = (e) => {
    this.setState({ url: e.target.value });
  };

  colorPicker = () => {
    let randomColor = this.state.color[
      Math.floor(Math.random() * this.state.color.length)
    ];
    return this.setState({
      currentColor: randomColor,
    });
  };

  submitHandler = (e) => {
    e.preventDefault();

    const key = encode(this.state.encodeNumber);

    this.setState({
      munchList: [
        ...this.state.munchList,
        {
          key: key,
          link: this.state.url,
          id: this.state.encodeNumber,
          color: this.state.currentColor,
        },
      ],
      encodeNumber: this.state.encodeNumber + 531,
    });

    this.colorPicker();
  };
  render() {
    return (
      <div className="App">
        <Header />
        <SubHeader />
        <AddUrl />
      </div>
    );
  }
}

export default App;
