    //     for (let i = str.length - 1; i >= 0; i--) {
    //         reverse += str.charAt(i);
    //     }
    //     return reverse;
    // }

    urlHandler = (e) => {
        this.setState({ url: e.target.value });
    };

    colorPicker = () => {
        let randomColor = this.state.color[
            Math.floor(Math.random() * this.state.color.length)
        ];
        return this.setState({
            currentColor: randomColor,
        });
    };

    submitHandler = (e) => {
        e.preventDefault();

        const key = encode(this.state.encodeNumber);

        this.setState({
            munchList: [
                ...this.state.munchList,
                {
                    key: key,
                    link: this.state.url,
                    id: this.state.encodeNumber,
                    color: this.state.currentColor,
                },
            ],
            encodeNumber: this.state.encodeNumber + 531,
        });

        this.colorPicker();
    };

    render() {
        let parser = Url;

        return (
            <div>
                <form onSubmit={this.submitHandler}>
                    <input
                        value={this.state.url}
                        className="addUrl"
                        onChange={this.urlHandler}
                        type="text"
                        placeholder="Enter Url..."
                    />
                </form>
                <h2>Url list</h2>
                <h3>
                    See Where It Goes
                    <span role="img" aria-label="silly-face">
                        ðŸ¤ª
                    </span>
                </h3>
                <hr />
                <div className="container">
                    {this.state.munchList.map((url) => {
                        parser = new Url(url.link);
                        return (
                            <div key={url.id}>
                                <h5>{parser.hostname.replace("www.", "")}</h5>
                                <button className={`url-btn ${url.color}`}>
                                    <a
                                        rel="noopener noreferrer"
                                        target="_blank"
                                        href={url.link}
                                    >{`Munch.ly/${url.key}`}</a>
                                </button>
                            </div>
                        );
                    })}
                </div>
            </div>
        );
    }
}
export default AddUrl;
