import React, { Component } from "react";
import Url from "url-parse";
import Links from "../data/Links";
import encode from "../algorithm/stringShortener";

class AddUrl extends Component {
    state = {
        url: "",
        encodeNumber: 101021,
        munchList: Links,
        key: "",
        color: ["orange", "black", "lightpurple", "pink", "yellow", "darkred"],
        currentColor: "orange",
        isLoading: false,
    };

    urlHandler = (e) => {
        this.setState({ url: e.target.value });
    };

    colorPicker = () => {
        let randomColor = this.state.color[
            Math.floor(Math.random() * this.state.color.length)
        ];
        return this.setState({
            currentColor: randomColor,
        });
    };

    componentWillUpdate() {
        setTimeout(() => {
            this.setState({
                isLoading: false,
            });
        }, 5000);
    }

    submitHandler = (e) => {
        e.preventDefault();
        const key = encode(this.state.encodeNumber);

        this.setState({
            munchList: [
                {
                    key: key,
                    link: this.state.url,
                    id: this.state.encodeNumber,
                    color: this.state.currentColor,
                },
                ...this.state.munchList,
            ],
            encodeNumber: this.state.encodeNumber + 531,
            isLoading: true,
        });

        this.colorPicker();
    };

    render() {
        let parser = Url;
        console.log("addUrl rendering");
        return (
            <>
                <form onSubmit={this.submitHandler}>
                    <input
                        value={this.state.url}
                        className="addUrl"
                        onChange={this.urlHandler}
                        type="text"
                        placeholder="Enter Url eg. https://www.epicgames.com/..."
                    />
                </form>
                <h2>list of Url Around The Web</h2>
                <h3>
                    See Where It Goes
                    <span role="img" aria-label="silly-face">
                        🤪
                    </span>
                </h3>
                <hr />
                {this.state.isLoading ? (
                    <div className="loader">isLoading...</div>
                ) : (
                    <ol className="container" reversed>
                        {this.state.munchList.map(
                            ({ id, link, color, key }) => {
                                parser = new Url(link);
                                return (
                                    <li key={id}>
                                        <h5>
                                            {parser.hostname.replace(
                                                "www.",
                                                ""
                                            )}
                                        </h5>
                                        <button className={`url-btn ${color}`}>
                                            <a
                                                rel="noopener noreferrer"
                                                target="_blank"
                                                href={link}
                                            >{`Munch.ly/${key}`}</a>
                                        </button>
                                    </li>
                                );
                            }
                        )}
                    </ol>
                )}
            </>
        );
    }
}
export default AddUrl;
