import React, { useState } from 'react';

const AddUrl = () => {
    const DICTIONARY = 'abcdefghijklmnopqrstuvwuxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';

    // const munchList = []

    function encode(num) {
        const base = DICTIONARY.length;
        let encoded = '';

        if (num === DICTIONARY[0]) {
            return DICTIONARY[0]
        }

        while (num > 0) {
            encoded += DICTIONARY[num % base];
            num = Math.floor(num / base)
        }

        return reverseWord(encoded)
    }

    function reverseWord(str) {
        let reverse = ""
        for (let i = str.length - 1; i >= 0; i--) {
            reverse += str.charAt(i)

        }
        return reverse
    }

    function decode(id) {
        const base = DICTIONARY.length
        let decoded = 0

        for (let i = 0; i < id.split("").length; i++) {
            decoded = decoded * base + DICTIONARY.indexOf(id.charAt(i))
        }

        return decoded
    }




    const [url, setUrl] = useState('')
    const [encodeNumber, setEncodeNumber] = useState(1000)
    const [munchList, setMunchList] = useState([])

    const urlHandler = (e) => {
        setUrl(e.target.value)
    }

    const submitHandler = (e) => {
        e.preventDefault()
        //console.log(url)

        setMunchList({
            [encode(encodeNumber)]: url
        })


        setEncodeNumber(encodeNumber + 583)

        console.log(munchList)
    }

    return (
        <div>
            <form onSubmit={submitHandler}>
                <input value={url} className="addUrl" onChange={urlHandler} type="text" placeholder="Enter Url..." />
            </form>
            <h1>Url list</h1>
            <hr />
            <div className="container">
                <div>
                    {munchList.map(list => (
                        <>
                            <h3>google </h3>
                            <button className="url-btn"><a target="_blank" href={list}>{`Munch.ly/${list}`}</a></button>
                        </>
                    ))}

                </div>
            </div>
        </div>
    );
};

export default AddUrl;
