import React, { Component } from "react";
import parse from "url-parse";
const DICTIONARY = "abcdefghijklmnopqrstuvwuxyzABCDEFGHIJKLMNOPQRSTUVWXYZ";

let parser = parse;
// const randomColor = ["lightblue", "black", "lightpurple"];
// let style = randomColor[Math.floor(Math.random() * randomColor.length)];
class AddUrl extends Component {
    constructor(props) {
        super(props);
    }
    state = {
        url: "",
        encodeNumber: 101023,
        munchList: [],
        key: "",
        color: ["lightblue", "black", "lightpurple"],
    };

    encode(num) {
        const base = DICTIONARY.length;
        let encoded = "";

        if (num === DICTIONARY[0]) {
            return DICTIONARY[0];
        }

        while (num > 0) {
            encoded += DICTIONARY[num % base];
            num = Math.floor(num / base);
        }

        return this.reverseWord(encoded);
    }

    reverseWord(str) {
        let reverse = "";
        for (let i = str.length - 1; i >= 0; i--) {
            reverse += str.charAt(i);
        }
        return reverse;
    }

    urlHandler = (e) => {
        this.setState({ url: e.target.value });
    };

    submitHandler = (e) => {
        e.preventDefault();

        const key = this.encode(this.state.encodeNumber);

        //console.log("key", key);
        this.setState({
            munchList: [
                ...this.state.munchList,
                {
                    key: key,
                    link: this.state.url,
                    id: this.state.encodeNumber,
                },
            ],
            encodeNumber: this.state.encodeNumber + 531,
        });

        console.log(this.state.munchList);
        this.colorPicker();
    };

    colorPicker = () => {
        let randomColor = this.state.color[
            Math.floor(Math.random() * this.state.color.length)
        ];
        this.setState({
            color: randomColor,
        });
    };

    render() {
        // style = randomColor[Math.floor(Math.random() * randomColor.length)];
        // console.log("style", style);
        return (
            <div>
                <form onSubmit={this.submitHandler}>
                    <input
                        value={this.state.url}
                        className="addUrl"
                        onChange={this.urlHandler}
                        type="text"
                        placeholder="Enter Url..."
                    />
                </form>
                <h1>Url list</h1>
                <h2>
                    See Where It Goes
                    <span role="img" aria-label="silly-face">
                        ðŸ¤ª
                    </span>
                </h2>
                <hr />
                <div className="container">
                    {this.state.munchList.map((url) => {
                        parser = parse(
                            "hostname",
                            this.state.munchList[url.key]
                        );
                        console.log(parser.hostname);
                        return (
                            <div key={url.id}>
                                <h3>{parser.hostname}</h3>
                                <button
                                    className={`url-btn ${this.colorPicker}`}
                                >
                                    <a
                                        rel="noopener noreferrer"
                                        target="_blank"
                                        href={url.link}
                                    >{`Munch.ly/${url.key}`}</a>
                                </button>
                            </div>
                        );
                    })}
                </div>
            </div>
        );
    }
}
export default AddUrl;
