import React, { Component } from "react";

const DICTIONARY = "abcdefghijklmnopqrstuvwuxyzABCDEFGHIJKLMNOPQRSTUVWXYZ";

class AddUrl extends Component {
    state = {
        url: "",
        encodeNumber: 101023,
        munchList: [],
    };

    encode(num) {
        const base = DICTIONARY.length;
        let encoded = "";

        if (num === DICTIONARY[0]) {
            return DICTIONARY[0];
        }

        while (num > 0) {
            encoded += DICTIONARY[num % base];
            num = Math.floor(num / base);
        }

        return this.reverseWord(encoded);
    }

    reverseWord(str) {
        let reverse = "";
        for (let i = str.length - 1; i >= 0; i--) {
            reverse += str.charAt(i);
        }
        return reverse;
    }

    urlHandler = (e) => {
        this.setState({ url: e.target.value });
    };

    submitHandler = (e) => {
        e.preventDefault();

        const key = this.encode(this.state.encodeNumber);
        //console.log("key", key);
        this.setState({
            munchList: [
                ...this.state.munchList,
                {
                    key: this.state.url,
                },
            ],
            encodeNumber: this.state.encodeNumber + 531,
        });

        // list = url
        // munchList.push(list)
        // setEncodeNumber(encodeNumber + 583)

        console.log(this.state.munchList);
    };

    render() {
        return (
            <div>
                <form onSubmit={this.submitHandler}>
                    <input
                        value={this.state.url}
                        className="addUrl"
                        onChange={this.urlHandler}
                        type="text"
                        placeholder="Enter Url..."
                    />
                </form>
                <h1>Url list</h1>
                <hr />
                <div className="container">
                    <div>
                        {/* {munchList.map(list => (
                        <>
                            <h3>google </h3>
                            <button className="url-btn"><a target="_blank" href={list}>{`Munch.ly/${list}`}</a></button>
                        </>
                    ))} */}
                    </div>
                </div>
            </div>
        );
    }
}
export default AddUrl;
